import { useState } from "react";
import { estadisticasFecha } from "../utils";

export default function Formulario(){

  const [dia,setDia] = useState("");
  const [mes,setMes] = useState("");
  const [anio,setAnio] = useState("");
  const [fechasIngresdas, setFechasIngresdas] = useState([]);

  const test = (e) => {

    e.preventDefault();
    peticionFormulario();
    function peticionFormulario(e){ 
      estadisticasFecha(dia, mes, anio, fechasIngresdas); 
    }

    if(!fechasIngresdas[0]){
      setFechasIngresdas([
        { 
          dia: dia,
          mes: mes,
          anio: anio
        }
      ]);
    }

    console.log(fechasIngresdas)
  }


  return(
   <>
    <h1 className="display-5 fw-bold text-center">Formulario</h1>
    <form id="Formulario">
      <hr/>
      <input
      type="text" 
      className="form-control" 
      id="dia"
      name="dia"
      placeholder="Indique Dia"
      value={dia}
      onChange={(e) => setDia(e.target.value)}
      />

      <br/>
      <input
      type="text" 
      className="form-control" 
      id="mes"
      name="mes"
      placeholder="Indique Mes"
      value={mes}
      onChange={(e) => setMes(e.target.value)}
      />

      <br/>
      <input
      type="text" 
      className="form-control" 
      id="anio"
      name="anio"
      placeholder="Indique Año"
      value={anio}
      onChange={(e) => setAnio(e.target.value)}
      />

      <br/><br/>
      <button
        type="submit"
        className="btn btn-primary"
        //onClick={peticionFormulario}
        onClick={test}
      >Enviar
      </button>
    </form>

    <div>
      <table id="tablaCalendario">
        <thead>
          <tr>
            <th>Dia</th>
            <th>Mes</th>
            <th>año</th>
          </tr>
        </thead>
        <tbody>
          {
            fechasIngresdas.map((unaFecha) => {
              return(
                <tr>
                  <td>{unaFecha.dia}</td>
                  <td>{unaFecha.mes}</td>
                  <td>{unaFecha.anio}</td> 
                </tr>  
              ) 
            })
          }
        </tbody>
      </table>
    </div> 
   </>
  );
}
