import {useState} from "react";
import { estadisticasFecha } from "../utils";

export default function Formulario(){

  const [dia,setDia] = useState("");
  const [mes,setMes] = useState("");
  const [anio,setAnio] = useState("");
  const [mostrar, setMostrar] = useState(false);

  const [fechasIngresdas, setFechasIngresdas] = useState([
    { dia: dia,
    mes: mes,
    anio: anio   },
  ]);



  //const [list, setList] = useState([]);



  function peticionFormulario(e){ 
    e.preventDefault(); 
    setMostrar(true);
    //agregarDatos(dia, mes, anio);
    estadisticasFecha(dia, mes, anio);  
    /*
    const formu = document.getElementById("Formulario");
    formu.addEventListener("submit", function(event){
      let fechasData = new FormData(formu);
      let tablaCalend = document.getElementById("tablaCalendario");
      let fila = tablaCalend.insertRow(-1);
      let celda =fila.insertCell(0);
      celda.textContent = fechasData.get({dia}) 
      event.preventDefault();*/

    // Peticiones al formulario

  }

  /*function agregarDatos({fechas}){
    let tablaCalend = document.getElementById("tablaCalendario");
    let fila = tablaCalend.insertRow(-1);
    let celda =fila.insertCell(2);
    celda.textContent = {fechas}
  }*/

  //const num = [1,2,3,4,5]
  //const funcmap = num.map(x => x * 2 )

  return(
   <>
    <h1 className="display-5 fw-bold text-center">Formulario</h1>
    <form id="Formulario">
      <hr/>
      <input
      type="text" 
      className="form-control" 
      id="dia"
      name="dia"
      placeholder="Indique Dia"
      value={dia}
      onChange={(e) => setDia(e.target.value)}
      />

      <br/>
      <input
      type="text" 
      className="form-control" 
      id="mes"
      name="mes"
      placeholder="Indique Mes"
      value={mes}
      onChange={(e) => setMes(e.target.value)}
      />

      <br/>
      <input
      type="text" 
      className="form-control" 
      id="anio"
      name="anio"
      placeholder="Indique Año"
      value={anio}
      onChange={(e) => setAnio(e.target.value)}
      />

      <br/><br/>
      <button
        type="submit"
        className="btn btn-primary"
        onClick={peticionFormulario}
      >Enviar
      </button>

    </form>




    <div>
      <table id="tablaCalendario">
        <thead>
          <tr>
            <th>Dia</th>
            <th>Mes</th>
            <th>año</th>
          </tr>
        </thead>
        <tbody>
          {fechasIngresdas.map( (unaFecha) => {
            return(
            <tr>
             <td>{unaFecha.dia}</td>
             <td>{unaFecha.mes}</td>
             <td>{unaFecha.anio}</td> 
            </tr>  
            ) 
          }

          )}

        </tbody>
      </table>
    </div>



   </>
  );
}
