import react, {useState} from "react";



export default function Formulario(){

  const [dia,setDia] = useState("");
  const [mes,setMes] = useState("");
  const [anio,setAnio] = useState("");

  let fechas = [];  //guardamos las fechas que se fueron ingresando.

  function peticionFormulario(e){
    // Peticiones al formulario
    e.preventDefault();

    let tablaCalend = document.getElementById("tablaCalendario"); //Selecciona la tabla y la guarda en variable
    let fila = tablaCalend.insertRow(-1); //Inserta  una fila

    let celda =fila.insertCell(0); //Inserta columnas
    celda.textContent = {dia}

    celda = fila.insertCell(1);
    celda.textContent = {mes}

    celda = fila.insertCell(2);
    celda.textContent = {anio}


  }


  function  esBisiesto(anio){ //U: devuelve true si el anio es bisiesto, false caso contrario
    if (anio % 4 == 0){
      return true;
    }
    return false  
  }

  function calcularDiasDelMes(mes, anio){ //A: devuelve entero que representa los dias del mes que pasron como paramnetro
    let anioEsBisiesto= esBisiesto(anio);
    if(mes == "4" || mes == "6" || mes == "9" || mes == "11"){
      return 30;
    }else if (mes == "2" && anioEsBisiesto){
      return 29;
    }else if (mes == "2" ){
      return 28;
    }else{
      return 31;
    }
  }


  function  calcularFechaJuliana(dia, mes, anio){
    let Jdia= parseInt(dia);
    let Jmes= parseInt(mes);
    let Janio= parseInt(anio);

    let Y = Janio - 1; 

    let M = Jmes + 12; 

    let D = Jdia; 

    let A = Y / 100; 
    A = A.toFixed();

    let B = 2 - A + A/4; 


    let dj = 0;
    dj = (365.25*(Y+4716))+(30.6001*(M+1))+D+(B)-1524.5;
    return dj;
  }

  function main(){

    e.preventDefault();      

    alert("La Fecha es: "+dia+"/"+mes+"/"+anio);

    let diasDelMes= calcularDiasDelMes(mes, anio);
    alert("el mes: " + mes + " , tiene " + diasDelMes);
    //A: mostramos los dias del mes

    let anioEsBisiesto= esBisiesto(anio);
    if (anioEsBisiesto) alert("el anio: " + anio + " , es bisiesto")
    else alert("el anio: " + anio + " , NO es bisiesto")
    //A: mostramos si el anio es bisiesto

    let diaJuliano= calcularFechaJuliana(dia, mes, anio);
    alert( "dia juliano: " + diaJuliano);

    obtener_LocalStorage()
    guardar_LocalStorage(dia, mes, anio);



    console.log(dia, mes, anio)
  }

  const handleSubmit = (e) => {
    main(dia, mes, anio );





    function guardar_LocalStorage(dia, mes, anio){
      let guardar_Storage = {dia, mes, anio};

      fechas.push(guardar_Storage);
      localStorage.setItem("fechas", JSON.stringify(fechas));
    }

    function obtener_LocalStorage(){

      if(localStorage.getItem("fechas")){
        fechas = JSON.parse( localStorage.getItem("fechas"));
      }else{
        console.log("No hay entradas en localstorage")
      }
    return fechas;
    }




  };




  return(
   <>
    <h2>Formulario</h2>
    <form id="Formulario" onSubmit={handleSubmit} >
      <hr/>

      <label htmlFor="dia">Dia </label>
      <input 
      type="text"
      id="dia"
      name="dia"
      value={dia}
      onChange={(e) => setDia(e.target.value)}
      />


      <br/><br/>

      <label htmlFor="mes">Mes </label>
      <input 
      type="text"
      id="mes"
      name="mes"
      value={mes}
      onChange={(e) => setMes(e.target.value)}
      />

      <br/><br/>

      <label htmlFor="anio">anio </label>
      <input 
      type="text"
      id="anio"
      name="anio"
      value={anio}
      onChange={(e) => setAnio(e.target.value)}
      />

      <br/><br/>
      <input type="submit" onClick={peticionFormulario}/>


    </form>



    <div>
      <table id="tablaCalendario">
        <tbody>
          <tr>
            <th>
              Dia
            </th>
            <th>
              Mes
            </th>
            <th>
              Año
            </th>
          </tr>
          <tr>
            <td>
              01
            </td>
            <td>
              01
            </td>
            <td>
              2022
            </td>
          </tr>
        </tbody>
      </table>


    </div>



   </>
  );
}
