import {useState} from "react";
import { estadisticasFecha } from "../utils";

export default function Formulario(){

  const [dia,setDia] = useState("");
  const [mes,setMes] = useState("");
  const [anio,setAnio] = useState("");
  const [mostrar, setMostrar] = useState(false)

  function peticionFormulario(e){ 
    e.preventDefault(); 
    setMostrar(true);
    estadisticasFecha(dia, mes, anio);  
    // Peticiones al formulario

    let tablaCalend = document.getElementById("tablaCalendario");
    let fila = tablaCalend.insertRow(-1);
    let celda =fila.insertCell(0);
    let con =celda.textContent = {dia}

  }




  return(
   <>
    <h1 className="display-5 fw-bold text-center">Formulario</h1>
    <form id="Formulario">
      <hr/>
      <input
      type="text" 
      className="form-control" 
      id="dia"
      name="dia"
      placeholder="Indique Dia"
      value={dia}
      onChange={(e) => setDia(e.target.value)}
      />

      <br/>
      <input
      type="text" 
      className="form-control" 
      id="mes"
      name="mes"
      placeholder="Indique Mes"
      value={mes}
      onChange={(e) => setMes(e.target.value)}
      />

      <br/>
      <input
      type="text" 
      className="form-control" 
      id="anio"
      name="anio"
      placeholder="Indique Año"
      value={anio}
      onChange={(e) => setAnio(e.target.value)}
      />

      <br/><br/>
      <button type="submit" className="btn btn-primary" onClick={peticionFormulario}>Enviar</button>

    </form>
      {mostrar &&
        <div>
          <table id="tablaCalendario">
            <thead>
              <tr>
                <th>Dia</th>
                <th>Mes</th>
                <th>año</th>
              </tr>
            </thead>
            <tbody>
              <tr>
              <td>{con}</td>
              <td>{mes}</td>
              <td>{anio}</td> 
              </tr>
            </tbody>
          </table>
        </div>

      }


   </>
  );
}
