        console.log(D);

        let A = Y / 100; 
        A = A.toFixed();
        console.log(A);

        let B = 2 - A + A/4; 
        console.log(B);


        let dj = 0;
        dj = (365.25*(Y+4716))+(30.6001*(M+1))+D+(B)-1524.5;
        return dj;
    }




